import React from "react";
import netflix from "../netflix.svg";
import { PlayIcon } from "@heroicons/react/outline";

const ApplyNowButtonCard = ({
  Height,
  Width,
  FontSize,
  FontWeight,
  ButtonText,
  JobTitle,
  FontStyle,
  CompanyName,
  Font16,
  Industry,
}) => {
  return (
    <div
      style={{
        height: Height,
        width: Width,
        fontSize: Font16,
        fontWeight: FontWeight,
        fontStyle: FontStyle,
      }}
      className="border border-solid border-gray-300 rounded-xl"
    >
      <div className="my-4 mx-6">
        {/* <img src={netflix} alt=""/> */}
        <div className="flex flex-row">
          <PlayIcon className="h-6 w-6 text-red-500" />
          <div>
            <div
              className="ml-2"
              style={{
                fontSize: FontSize,
                fontWeight: FontWeight,
                fontStyle: FontStyle,
              }}
            >
              {JobTitle}
            </div>
            <div style={{ fontSize: Font16 }}>
              {CompanyName} - <span>{Industry}</span>
            </div>
          </div>
        </div>
        <button
          class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          type="button"
        >
          {ButtonText}
        </button>
      </div>
    </div>
  );
};

export default ApplyNowButtonCard;
